import { Button, CircularProgress, CssBaseline, Grid, TextField, Typography } from '@material-ui/core'
import React from 'react'
import useStyles from './style'
import {textFormValidator,numberValidator} from '../../FormValidators/FormValidator'
function Index({
    handleFunction,
    register,
    formData,
    loading,
    collectionName,
    id
}) {

    const classes = useStyles()
    return (
        <Grid spacing={3}>
        <CssBaseline />
        <div className={classes.paper}>
          <Typography className={classes.heading} color="primary" component="h1" variant="h5">
            Add New {collectionName.slice(0,-1)}
          </Typography>
          <form className={classes.form} onSubmit={handleFunction}>
            <Grid container spacing={2}>
                        {formData.map((detail) => {
                            return <Grid item xs={12} >
                                <TextField
                                    variant="outlined"
                                    fullWidth
                                    type={detail === "price" ?"number":"text"}
                                    label={`Enter Your ${detail}*`}
                                    name={detail}
                                    autoComplete="off"
                                    inputRef={register(detail === "price"?numberValidator():textFormValidator())}
                                    error={detail.error}
                                    helperText={detail.helperText}
                                />

                            </Grid>
                        })}
                        <Grid item xs={12} >
                        <Typography component="h5" variant="h6">select image</Typography>
                                <input
                                    accept="image/*"
                                    className={classes.input}
                                    multiple
                                    type="file"
                                    name="image"
                                    required
                                    ref={register}
                            />
                            </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              disabled={loading}
              className={classes.submit}
            >
               <CircularProgress color="primary" size="22px" className={classes.loading}  style={loading?{display:"block"}:{display:"none"}}/>
              save
            </Button>
          </form>
        </div>
      </Grid>
    )
}

export default Index
