};
app.initializeApp(config);

const firebaseDb = app.firestore()
const storageRef = app.storage().ref()

export default app;

export async function addUser(email,password){
    try{
    let userData = await app.auth().createUserWithEmailAndPassword(email,password)
    await firebaseDb.collection('users').doc(userData.user.uid).set({
        email,
        password,
        createdAt: app.firestore.FieldValue.serverTimestamp()
            })
    }
    catch(error){
        alert(error.message)
    }
}
export async function logoutUser(){
    try{
        await app.auth().signOut()
    }
    catch(err){
        alert(err.message)
    }
}
export async function loginUser(email,password){
    try{
        await app.auth().signInWithEmailAndPassword(email,password)
    }
    catch(error){
        alert(error.message)
    }
}

export async function addNote(noteData){
    await firebaseDb.collection('notes')
    .add(
        {
           ...noteData,
           createdAt: app.firestore.FieldValue.serverTimestamp()
        })
}
export async function getUserNotes(id){
    let data =[]
    let userdata = await firebaseDb.collection('notes').where("userId","==",id).orderBy("createdAt","desc").get()
    userdata.forEach((doc)=>{
        data = [
            ...data,
            {
            id:doc.id,
            text:doc.data().text,
            date:doc.data().date,
            userId:doc.data().userId,
        }]
    })
    return data
}
export async function getNote(id){
    let data 
    let doc = await firebaseDb.collection('notes').doc(id).get()
        data = {
            id:doc.id,
            text:doc.data().text,
            date:doc.data().date,
            userId:doc.data().userId,
            imageUrl:doc.data().imageUrl
        }
        console.log(doc.data())
    return data
}
export async function updateNote(id,noteData){
    await firebaseDb.collection('notes').doc(id).set(noteData,{merge:true})
}
export async function deleteNote(id,imageUrl){
    if(imageUrl.length!==0){
        imageUrl.map(async(url)=>{
            const fileRef = app.storage().refFromURL(url)
            try{
            await fileRef.delete()
            }
            catch(err){
                console.log(err)
            }
        })
    }
    await firebaseDb.collection('notes').doc(id).delete()

}

export async function uploadImage(userId,date,imageData){
    const fileRef = storageRef.child(`/${userId}/images/${date}/${imageData.name}`);
    await fileRef.put(imageData)
    let url = await fileRef.getDownloadURL()
    return url
}
