import React, { useState, useEffect } from 'react'
import { Grid, Typography } from '@material-ui/core'
import MatchCard from '../../Components/MatchCard'
import Loader from '../../Components/Loader'
import useStyles from './style'
import { getAllDocuments, getSingleDocument } from '../../services/firebase'
import { SettingsSystemDaydreamOutlined } from '@material-ui/icons'

export default function Index({
    loggedinUser,
    externalData,
    location,
    ...rest
}){
    const classes = useStyles()
    const [plants, setPlants] = useState([])
    const [isAdmin, setIsAdmin] = useState(false)
    const [loading, setLoading] = useState(false)
    const [snackBar, setSnackBar] = React.useState({
        value: false,
        message: '',
        type: '',
    })

    useEffect(() => {
        var mounted = true
        async function getPlants() {
            setLoading(true)
            const isAdminResponse = await getSingleDocument('users',loggedinUser.userId)
            if (isAdminResponse.isAdmin) {
                setIsAdmin(true)
            }
            const response = await getAllDocuments('plants')
            console.log(response)
            if (mounted) {
                setPlants(response)
            }
            setLoading(false)
        }
        if (!externalData) {
            getPlants()
        }
        return () => { mounted = false }
    },[])



    if (!loading) {
        const filteredData = externalData ? externalData : plants
        if (filteredData.length !== 0) {
            return (
                <div className={classes.root}>
                    <Grid container spacing={2}>
                        {filteredData.map((plant,index) => {
                            return (
                                <Grid
                                    container
                                    item
                                    key={index}
                                    xs={12}
                                    sm={6}
                                    lg={3}
                                    spacing={5}
                                    className={classes.wrapper}
                                >
                                    <MatchCard
                                        isAdmin={isAdmin}
                                        key={plant.id}
                                        plantData={plant}
                                        loggedinUser={loggedinUser}
                                        history={rest.history}
                                    />
                                </Grid>
                            )
                        })}
                    </Grid>
                </div>
            )
        } else {
            return (
                <Typography
                    component='h1'
                    color='secondary'
                    variant='h4'
                    align='center'
                >
                    No Plants Found
                </Typography>
            )
        }
    } else {
        return (
            <Typography align='center'>
                <Loader />
            </Typography>
        )
    }
}
